mapper.select:
SELECT

from endocrine_info t
WHERE 1=1

    and  t.patient_id = #{query.patientId}


    and t.input_status = #{query.inputStatus}


    and t.is_enable = #{query.isEnable}


    and t.is_del = #{query.isDel}

order by t.created desc

SELECT t.id
FROM endocrine_info t
WHERE t.patient_id = #{patientId}

    AND t.is_del = #{isDel}

SELECT

FROM endocrine_info t
WHERE t.patient_id = #{patientId}

    AND t.is_del = #{isDel}

order by t.created desc

SELECT g.input_status FROM patient_info p LEFT JOIN general_info g ON p.id = g.patient_id WHERE p.id = #{patientId}
UNION ALL
SELECT c.input_status FROM patient_info p LEFT JOIN case_info c ON p.id = c.patient_id WHERE p.id = #{patientId}
UNION ALL
SELECT s.input_status FROM patient_info p LEFT JOIN surgical_info s ON p.id = s.patient_id WHERE p.id = #{patientId}
UNION ALL
SELECT n.input_status FROM patient_info p LEFT JOIN neoadjuvant_info n ON p.id = n.patient_id WHERE p.id = #{patientId}
UNION ALL
SELECT a.input_status FROM patient_info p LEFT JOIN adjuvant_info a ON p.id = a.patient_id WHERE p.id = #{patientId}
UNION ALL
SELECT e.input_status FROM patient_info p LEFT JOIN endocrine_info e ON p.id = e.patient_id WHERE p.id = #{patientId}
UNION ALL
SELECT r.input_status FROM patient_info p LEFT JOIN radiation_info r ON p.id = r.patient_id WHERE p.id = #{patientId}

mapper.update:
UPDATE patient_info
SET input_status = #{inputStatus}
WHERE id = #{id}
